def romaji(line, l):

    # Leadin Effect
    FU = FrameUtility(line.start_time - 500,
                      line.start_time - 250)
    for s, e, i, n in FU:

        # l.layer = line.i
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\c&HFFFFFF&\\fscx%d\\clip%s}%s" % (
            line.center, line.middle, fscx[i-1], clip_awal[i-1], line.text)

        io.write_line(l)

    FU = FrameUtility(line.start_time - 250,
                      line.start_time)

    for s, e, i, n in FU:

        # l.layer = line.i+1
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\c&HFFFFFF&}%s" % (
            line.center, line.middle, line.text)

        io.write_line(l)

        # l.layer = line.i+2
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\clip%s}%s" % (
            line.center, line.middle, clip_akhir[i-1], line.text)

        io.write_line(l)

    for syl in Utils.all_non_empty(line.syls):
        # Leadin Effect (tanpa efek)
        # l.layer = line.i+3
        l.start_time = line.start_time
        l.end_time = line.start_time + syl.start_time
        l.text = "{\\an5\\pos(%.3f,%.3f)}%s" % (
            syl.center, syl.middle, syl.text)

        io.write_line(l)

        # Main Effect

        # l.layer = line.i+4
        l.start_time = line.start_time + syl.start_time
        l.end_time = line.start_time + syl.end_time
        durasi = l.end_time - l.start_time
        l.text = "{\\blur3\\bord4\\an5\\pos(%.3f,%.3f)\\c&H0198FE&\\t(0,%d,\\fscx125,\\fscy137)\\t(%d,%d,\\fscx100,\\fscy115)}%s" % (
            syl.center, syl.middle, durasi/3, durasi/3, durasi, syl.text)

        io.write_line(l)

        if line.effect == "ref":

            l.start_time = line.start_time + syl.start_time
            l.end_time = line.start_time + syl.end_time + 800
            l.dur = l.end_time - l.start_time

            posisix = random.uniform(-7, 7)
            posisiy = random.uniform(20, 30)
            atasbawah = [1, -1]
            pengali = random.choice(atasbawah)

            l.text = "{\\alpha&HFF&\\c&HFFFFFF&\\blur1\\bord1\\t(0,%d,\\bord2\\blur3\\alpha&H00&)\\t(%d,%d,\\bord1\\blur1\\alpha&HFF&)\\t(%d,%d,\\bord2\\blur3\\alpha&H00&)\\t(%d,%d,\\bord1\\blur1\\alpha&HFF&)\\t(%d,%d,\\bord2\\blur3\\alpha&H00&)\\t(%d,%d,\\bord1\\blur1\\alpha&HFF&)\\p1\\pos(%.3f,%.3f)}%s" % (
                l.dur/6, l.dur/6, l.dur/3, l.dur/3, l.dur/2, l.dur/2, (l.dur*2)/3, (l.dur*2)/3, (l.dur*5)/6, (l.dur*5)/6, l.dur, syl.center+posisix, syl.middle+(pengali*posisiy), Shape.star(5, 2, 4))

            io.write_line(l)

        # Leadout Effect
        # l.layer = line.i+3
        l.start_time = line.start_time + syl.end_time
        l.end_time = line.end_time + 200
        l.text = "{\\an5\\pos(%.3f,%.3f)\\fad(0,200)}%s" % (
            syl.center, syl.middle, syl.text)

        io.write_line(l)


def sub(line, l):
    # Translation Effect

    FU = FrameUtility(line.start_time - 500,
                      line.start_time - 250)
    for s, e, i, n in FU:

        # l.layer = line.i
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\c&HFFFFFF&\\fscx%d\\clip%s}%s" % (
            line.center, line.middle, fscx[i-1], clip_awal_tl[i-1], line.text)

        io.write_line(l)

    FU = FrameUtility(line.start_time - 250,
                      line.start_time)

    for s, e, i, n in FU:

        # l.layer = line.i+1
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\c&HFFFFFF&}%s" % (
            line.center, line.middle, line.text)

        io.write_line(l)

        # l.layer = line.i+2
        l.start_time = s
        l.end_time = e

        l.text = "{\\an5\\pos(%.3f,%.3f)\\clip%s}%s" % (
            line.center, line.middle, clip_akhir_tl[i-1], line.text)

        io.write_line(l)

    # l.layer = line.i+3
    l.start_time = line.start_time
    l.end_time = line.end_time + 200
    l.text = "{\\an5\\pos(%.3f,%.3f)\\fad(0,200)}%s" % (
        line.center, line.middle, line.text)

    io.write_line(l)


# Generating lines
for line in lines:
    if line.styleref.alignment >= 7:
        romaji(line, line.copy())
    else:
        sub(line, line.copy())
io.save()
io.open_aegisub()
